package Uebungen;import java.time.LocalDateTime;import java.time.Period;import java.time.format.DateTimeFormatter;import java.time.format.FormatStyle;public class _000_Beispiele {    public static  void main(String[] args) {        LocalDateTime d = LocalDateTime.of(2015, 5, 10, 11, 22, 33);        System.out.println(d);        Period p = Period.ofDays(1).ofYears(2);        System.out.println("------");        System.out.println(d);        System.out.println(p);        d = d.minus(p);        System.out.println(p);        DateTimeFormatter f = DateTimeFormatter.ofLocalizedDateTime(FormatStyle                .SHORT);        System.out.println(p);        System.out.println(f.format(d));    }}